mapper.select:
SELECT COUNT(*) FROM codegroup WHERE 1=1 AND is_used = #{searchVo.shIsUse} AND is_deleted = #{searchVo.shIsDel}
AND created_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59') AND updated_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59')
AND codegroup_id = #{searchVo.shValue} AND name LIKE CONCAT('%',#{searchVo.shValue},'%')

SELECT codegroup_id AS codegroupId ,name ,created_at AS createdAt ,updated_at AS updatedAt ,is_used AS isUsed FROM codegroup WHERE 1=1 AND is_used = #{searchVo.shIsUse} AND is_deleted = #{searchVo.shIsDel}
AND created_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59') AND updated_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59')
AND codegroup_id = #{searchVo.shValue} AND name LIKE CONCAT('%',#{searchVo.shValue},'%')
ORDER BY codegroup_id DESC LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}

mapper.update:
UPDATE codegroup SET
	name = #{name}
	,is_used = #{isUsed}
	,updated_at = now()
WHERE
	1=1
	AND codegroup_id = #{codegroupId}
