mapper.select:
SELECT COUNT(*) FROM code c JOIN codegroup cg ON cg.codegroup_id = c.codegroup_id WHERE 1=1 AND c.is_used = #{searchVo.shIsUse} AND c.is_deleted = #{searchVo.shIsDel}
AND c.created_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59') AND c.updated_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59')
AND c.codegroup_id = #{searchVo.shValue} AND cg.name LIKE CONCAT('%',#{searchVo.shValue},'%') AND c.code_id = #{searchVo.shValue} AND c.name LIKE CONCAT('%',#{searchVo.shValue},'%')

SELECT c.code_id AS codeId ,c.name ,c.created_at AS createdAt ,c.updated_at AS updatedAt ,c.is_used AS isUsed ,c.codegroup_id AS codegroupId ,cg.name AS codegroupName FROM code c JOIN codegroup cg ON cg.codegroup_id = c.codegroup_id WHERE 1=1 AND c.is_used = #{searchVo.shIsUse} AND c.is_deleted = #{searchVo.shIsDel}
AND c.created_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59') AND c.updated_at BETWEEN CONCAT(#{searchVo.shDateStart}, 'T00:00:00') AND CONCAT(#{searchVo.shDateEnd}, 'T23:59:59')
AND c.codegroup_id = #{searchVo.shValue} AND cg.name LIKE CONCAT('%',#{searchVo.shValue},'%') AND c.code_id = #{searchVo.shValue} AND c.name LIKE CONCAT('%',#{searchVo.shValue},'%')
ORDER BY code_id DESC LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}

mapper.update:
UPDATE code SET
	name = #{name}
	,is_used = #{isUsed}
	,updated_at = now()
	,codegroup_id = #{codegroupId}
WHERE
	1=1
	AND code_id = #{codeId}
