mapper.sql:
FROM user a
WHERE 1=1

AND a.userDelNy = #{shDelNy}
AND a.gender = #{shGender}
AND a.admin = #{shAdmin}




	AND a.email = #{shValue}
	AND a.nickname LIKE CONCAT('%',#{shValue},'%')
	AND CONCAT(a.lastNm, a.firstNm) LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO user(
	email
	,password
	,nickname
	,lastNm
	,firstNm
	,gender
	,birthYear
	,birthMonth
	,birthDay
	,mobileCarrier
	,phone
	,admin
	,userDelNy
) VALUES(
	#{email}
	,#{password}
	,#{nickname}
	,#{lastNm}
	,#{firstNm}
	,#{gender}
	,#{birthYear}
	,#{birthMonth}
	,#{birthDay}
	,#{mobileCarrier}
	,#{phone}
	,#{admin}
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE user SET
	email = #{email}
	,nickname = #{nickname}
	,lastNm = #{lastNm}
	,firstNm = #{firstNm}
	,gender = #{gender}
	,birthYear = #{birthYear}
	,birthMonth = #{birthMonth}
	,birthDay = #{birthDay}
	,mobileCarrier = #{mobileCarrier}
	,phone = #{phone}
	,admin = #{admin}
WHERE
	seq = #{ifcgSeq}
