mapper.sql:
FROM
	orders o
	JOIN user u ON u.seq = o.user_seq
WHERE 1=1

AND o.ordersDelNy = #{shDelNy}
AND o.ordersStatus = #{shStatus}
AND o.takeOut = #{shTakeOut}


	AND o.seq LIKE CONCAT('%',#{shValue},'%')
	AND u.nickname LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE orders SET
	ordersStatus = #{ordersStatus}
WHERE
	seq = #{seq}
