mapper.select:
SELECT 
	COUNT(*)
FROM
	order_master om
	JOIN member mb ON mb.member_id = om.member_id
WHERE
	1=1
	AND om.is_deleted = #{shIsDel}
	AND om.status = #{shStatus}




	            AND om.created_at >= CONCAT(#{shDateStart}, 'T00:00:00')


	            AND om.created_at <= CONCAT(#{shDateEnd}, 'T23:59:59')







	            AND om.price >= #{shRangeStart, jdbcType=INTEGER}


	            AND om.price <= #{shRangeEnd, jdbcType=INTEGER}





		AND om.order_master_id = #{shValue}
		AND mb.name LIKE CONCAT('%',#{shValue},'%')

SELECT om.order_master_id AS orderMasterId ,mb.name AS memberName ,om.status AS status ,om.price AS price ,om.created_at AS createdAt FROM order_master om JOIN member mb ON mb.member_id = om.member_id WHERE 1=1 AND om.is_deleted = #{searchVo.shIsDel} AND om.status = #{searchVo.shStatus}
AND om.created_at >= CONCAT(#{searchVo.shDateStart}, 'T00:00:00')
AND om.created_at <= CONCAT(#{searchVo.shDateEnd}, 'T23:59:59')
AND om.price >= #{searchVo.shRangeStart, jdbcType=INTEGER}
AND om.price <= #{searchVo.shRangeEnd, jdbcType=INTEGER}
AND om.order_master_id = #{searchVo.shValue} AND mb.name LIKE CONCAT('%',#{searchVo.shValue},'%')
ORDER BY om.order_master_id DESC LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}

mapper.insert:
INSERT INTO order_master(
	status
	,price
	,created_at
	,is_deleted
	,member_id
) VALUES (
	#{status}
	,#{price}
	,now()
	,0
	,#{memberId}
)

	SELECT last_insert_id()

mapper.update:
UPDATE order_master SET
	status = #{status}
WHERE
	order_master_id = #{orderMasterId}
